import React, {Component} from "react"

class App extends Component{

  constructor(props){
    super(props)
    this.state = {
      items: [],
      loading: false
    }
  }

  componentDidMount(){
    fetch("https://randomuser.me/api/?results=52")
    .then((res) => res.json())
    .then((res) => {
      this.setState({
        items: res.results,
        loading: true
      })
    })
  }

  render(){
    var {items, loading} = this.state;
    if(!loading){
      return(
        <div>Loading...</div>
      )} else {
        return(
          <div className="container">
          <div className="row justify-content-center align-items-center">
            {items.map(item => (
              <div className="userInfo col-lg-3 ml-auto mr-auto">
              <img className="avatar" src={item.picture.thumbnail} alt={item.name.first}/>
              <h6>Nombre: {item.name.first} {item.name.last}</h6>
              <p>Ciudad: {item.location.city}</p>
              <p>Nacionalidad: {item.nat}</p>
              </div>
            ))}
            </div>
            </div>
        )
      }
    }
}
export default App;
