import React, {Component} from "react"

class App extends Component{

  constructor(props){
    super(props)
    this.state = {
      items: [],
      loading: false
    }
  }

  componentDidMount(){
    fetch("https://randomuser.me/api/")
    .then((res) => res.json())
    .then((res) => {
      this.setState({
        items: res.results,
        loading: true
      })
    })
  }

  render(){
    var {items, loading} = this.state;
    if(!loading){
      return(
        <div>Loading...</div>
      )
    }
    return(
      <div>Hello world</div>
    )
  }
}

export default App;
